WebPart.SQL:
DECLARE @Number INT ,@Type NVARCHAR(30),@PersonType NVARCHAR(10); DECLARE @Temp TABLE(ID NVARCHAR(50),ModifiedDate datetime) SELECT @Type={{Type}},@PersonType={{PersonType}}; IF LEN(@Type)=0 BEGIN SELECT @Number=COUNT(*) FROM dbo.tblYLFCZD WHERE ZDSJ>GETDATE();
IF @Number>8 BEGIN SELECT TL.Description,FXBT,FCZJ,FCMJ,ID,dbo.fnYL_GetFCHX(YL.FCHU1,YL.FCHU2,YL.FCHU3) AS HouseLevelDetail,(SELECT TOP 1 'http://web.tool8.cc/UploadImages/Temp/'+ImageID+'/'+FileName FROM tblYLFCImage WHERE ItemKey=YL.ID) AS ImageSrc,N'优' AS WordSrc FROM tblYLHouses YL LEFT JOIN tblTicketLookup TL ON YL.XQXZ=TL.LookupKey AND TL.LookupCat=N'小区名称' INNER JOIN dbo.tblYLFCZD YLF ON YL.ID=YLF.FCID WHERE LEN(YL.XQXZ)>0 AND YL.ID IN (SELECT TOP 8 FCID FROM dbo.tblYLFCZD WHERE ZDSJ>GETDATE() ORDER BY ModifiedDate DESC) AND YL.Status='A' AND (YL.FCPublish LIKE '%' + @PersonType +'%' OR LEN(ISNULL(@PersonType,''))=0) ORDER BY YLF.ModifiedDate DESC; END ELSE BEGIN INSERT @Temp(ID,ModifiedDate) SELECT FCID,ModifiedDate FROM dbo.tblYLFCZD WHERE ZDSJ>GETDATE() ORDER BY ModifiedDate DESC;
SELECT * FROM ( SELECT 1 AS Flag,TL.Description,FXBT,FCZJ,FCMJ,YL.ID,T.ModifiedDate,dbo.fnYL_GetFCHX(YL.FCHU1,YL.FCHU2,YL.FCHU3) AS HouseLevelDetail,(SELECT TOP 1 'http://web.tool8.cc/UploadImages/Temp/'+ImageID+'/'+FileName FROM tblYLFCImage WHERE ItemKey=YL.ID) AS ImageSrc ,N'优' AS WordSrc FROM tblYLHouses YL LEFT JOIN tblTicketLookup TL ON YL.XQXZ=TL.LookupKey AND TL.LookupCat=N'小区名称' INNER JOIN @Temp T ON T.ID = YL.ID WHERE LEN(YL.XQXZ)>0 AND YL.Status='A' AND (YL.FCPublish LIKE '%' + @PersonType +'%' OR LEN(ISNULL(@PersonType,''))=0) UNION ALL SELECT TOP (8-@Number) 0 AS Flag,TL.Description,FXBT,FCZJ,FCMJ,YL.ID,YL.ModifiedDate,dbo.fnYL_GetFCHX(YL.FCHU1,YL.FCHU2,YL.FCHU3) AS HouseLevelDetail,(SELECT TOP 1 'http://web.tool8.cc/UploadImages/Temp/'+ImageID+'/'+FileName FROM tblYLFCImage WHERE ItemKey=YL.ID) AS ImageSrc ,N'' AS WordSrc FROM tblYLHouses YL LEFT JOIN tblTicketLookup TL ON YL.XQXZ=TL.LookupKey AND TL.LookupCat=N'小区名称' WHERE LEN(YL.XQXZ)>0 AND (YL.FCPublish LIKE '%' + @PersonType +'%' OR LEN(ISNULL(@PersonType,''))=0) AND YL.ID NOT IN(SELECT ID FROM @Temp) AND YL.Status='A') T ORDER BY Flag DESC,ModifiedDate DESC
END END ELSE BEGIN SELECT @Number=COUNT(*) FROM dbo.tblYLHouses YLH INNER JOIN dbo.tblYLFCZD YLF ON YLH.ID=YLF.FCID WHERE ZDSJ>GETDATE() AND YLH.HousesType=@Type;
IF @Number>8 BEGIN SELECT TL.Description,FXBT,FCZJ,FCMJ,ID,dbo.fnYL_GetFCHX(YL.FCHU1,YL.FCHU2,YL.FCHU3) AS HouseLevelDetail,(SELECT TOP 1 'http://web.tool8.cc/UploadImages/Temp/'+ImageID+'/'+FileName FROM tblYLFCImage WHERE ItemKey=YL.ID) AS ImageSrc ,N'优' AS WordSrc FROM tblYLHouses YL LEFT JOIN tblTicketLookup TL ON YL.XQXZ=TL.LookupKey AND TL.LookupCat=N'小区名称' INNER JOIN dbo.tblYLFCZD YLF ON YL.ID=YLF.FCID WHERE LEN(YL.XQXZ)>0 AND YL.ID IN (SELECT TOP 8 FCID FROM dbo.tblYLFCZD WHERE ZDSJ>GETDATE() ORDER BY ModifiedDate DESC) AND YL.HousesType=@Type AND YL.Status='A' AND (YL.FCPublish LIKE '%' + @PersonType +'%' OR LEN(ISNULL(@PersonType,''))=0) ORDER BY YLF.ModifiedDate DESC; END ELSE BEGIN INSERT @Temp(ID,ModifiedDate) SELECT YLF.FCID,YLF.ModifiedDate FROM dbo.tblYLHouses YLH INNER JOIN dbo.tblYLFCZD YLF ON YLH.ID=YLF.FCID WHERE ZDSJ>GETDATE() AND YLH.HousesType=@Type;
SELECT * FROM ( SELECT 1 AS Flag,TL.Description,FXBT,FCZJ,FCMJ,YL.ID,T.ModifiedDate,dbo.fnYL_GetFCHX(YL.FCHU1,YL.FCHU2,YL.FCHU3) AS HouseLevelDetail,(SELECT TOP 1 'http://web.tool8.cc/UploadImages/Temp/'+ImageID+'/'+FileName FROM tblYLFCImage WHERE ItemKey=YL.ID) AS ImageSrc ,N'优' AS WordSrc FROM tblYLHouses YL LEFT JOIN tblTicketLookup TL ON YL.XQXZ=TL.LookupKey AND TL.LookupCat=N'小区名称' INNER JOIN @Temp T ON T.ID = YL.ID WHERE LEN(YL.XQXZ)>0 AND YL.HousesType=@Type AND YL.Status='A' AND (YL.FCPublish LIKE '%' + @PersonType +'%' OR LEN(ISNULL(@PersonType,''))=0) UNION ALL SELECT TOP (8-@Number) 0 AS Flag,TL.Description,FXBT,FCZJ,FCMJ,YL.ID,YL.ModifiedDate,dbo.fnYL_GetFCHX(YL.FCHU1,YL.FCHU2,YL.FCHU3) AS HouseLevelDetail,(SELECT TOP 1 'http://web.tool8.cc/UploadImages/Temp/'+ImageID+'/'+FileName FROM tblYLFCImage WHERE ItemKey=YL.ID) AS ImageSrc ,N'' AS WordSrc FROM tblYLHouses YL LEFT JOIN tblTicketLookup TL ON YL.XQXZ=TL.LookupKey AND TL.LookupCat=N'小区名称' WHERE LEN(YL.XQXZ)> 0AND (YL.FCPublish LIKE '%' + @PersonType +'%' OR LEN(ISNULL(@PersonType,''))=0) AND YL.ID NOT IN(SELECT ID FROM @Temp) AND YL.HousesType=@Type AND YL.Status='A') T ORDER BY Flag DESC,ModifiedDate DESC
END END
